mapper.select:
SELECT * FROM biz_modal_identifier 
WHERE user_id = #{userId} 
AND identifier_type = #{projectType}
AND status = 'VALID'
ORDER BY modal_id DESC

SELECT * FROM biz_modal_identifier 
WHERE user_id = #{userId} 
AND status = 'VALID'
AND DATE(create_time) BETWEEN #{startDate} AND #{endDate}
ORDER BY modal_id DESC

SELECT m.modal_id AS modalId, m.modal_value AS modalValue, m.two_factor_value AS twoFactorCode, m.status, m.create_time AS createTime, p.project_id AS projectId, p.project_name AS projectName, p.project_type AS projectType, p.byte_size AS byteSize, p.project_desc AS projectDesc, p.cloud_storage_url AS cloudStorageUrl, p.project_status AS projectStatus FROM biz_modal_identifier m INNER JOIN biz_project_info p ON m.modal_id = p.modal_id WHERE m.user_id = #{userId} AND m.deleted = 0 AND p.deleted = 0
AND p.project_type = #{projectType}
AND DATE(m.create_time) BETWEEN #{startDate} AND #{endDate}
ORDER BY m.create_time DESC
